import "./App.css";
import { useState } from 'react';

function App() {

  const [img, setImg] = useState([]);
  const [left, setLeft] = useState(0);
  const [right, setRight] = useState(0);

  const downloadImgFunction = (e) => {
    if (e.target.files.length !== 0) {
      setImg(imgfile => [...imgfile, URL.createObjectURL(e.target.files[0])])
    }
  }

  const rotateRight = () => {

  }

  const rotateLeft = () => {

    setLeft(left - 90);
  }

  return (
    <div className="App">

      <div>
        <div id="titleAndSelectImg">
          <h2>Upload Something </h2>
          <div id="inputFile">
            Add Images
            <input type="file" onChange={downloadImgFunction} />
          </div>
        </div>
        <div id="main">
          <h2>Img colection</h2>
          {img.map((elem) => {
            return <>
              <div id="itemContainer" key={elem}>

                <div><button id='leftButton' onClick={rotateLeft} > Turn Left </button></div>
                <div id="imgContainer"><img src={elem} alt="med1" /></div>
                <div><button id='rightButton'> Turn right </button></div>

              </div>
            </>
          })}
        </div>
      </div>
    </div>
  );
}
export default App;
